import React from 'react'

const Display = ({ data }) => {

    // console.log(data)

    const { loader, resp, err } = data
    let results = []
    if(resp.length){
        for(let eachResp of resp){
            if(eachResp.meanings){
                for(let grpMeaning of eachResp.meanings){
                    console.log(grpMeaning)
                }
            }
        }
    }

    return (
        <div className='row'>
            <div className='col-12'>
                {
                    loader ? (<div>Loading....</div>) :

                        resp ? (

                            <>
                            {
                                resp.map(section => {
                                    if (section.meanings) {
                                        section.meanings.map(type => {
                                            return (
                                                <>
                                                    <h6>{type?.partOfSpeech}</h6>
                                                    <ul>
                                                        {
                                                            type?.definitions.map(res => {
                                                                return <li key={res.definition}>{res.definition}</li>
                                                            })
                                                        }
                                                    </ul>
                                                </>
                                            )
                                        })
                                    }
                                })
                            }
                            </>


                        ) :

                            err ? (<div>{err}</div>) : null
                }
            </div>
        </div>
    )
}

export default Display
